CREATE TABLE jiangsu.taizhoushi_100 AS SELECT * FROM (SELECT ROUND(sum(area)) AS sum_area, ST_ConcaveHull(ST_Collect(ST_Transform(ST_SetSRID(geom, 4326), 3857)), 0.99) as geom, cid FROM (SELECT *, ST_ClusterDBSCAN(ST_Transform(ST_SetSRID(geom, 4326), 3857), eps := 50, minpoints := 2) over () AS cid FROM jiangsu.taizhoushi) cluster WHERE cid IS NOT NULL GROUP BY cid) area WHERE sum_area>100